delete from cte.pontuacao where ptoid in (
	select ptoid from cte.pontuacao p
	inner join cte.indicador i on i.indid = p.indid
	inner join cte.areadimensao ad on ad.ardid = i.ardid
	inner join cte.dimensao d on d.dimid = ad.dimid
	inner join cte.instrumento it on it.itrid = d.itrid and it.itrid in (3,4)
);

